import { ImageResponse } from "@vercel/og";
import { NextRequest } from "next/server";
import parse from "node-html-parser";

export const config = {
  runtime: "experimental-edge",
};

const colCount = 7;

const padding = "0.31rem";

function Col({ urls, width }: { urls: string[]; width?: string }) {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        padding: `0 ${padding}`,
        width: width ?? `${100 / colCount}%`,
        marginTop: -100 + Math.random() * -30,
      }}
    >
      {urls.map((url) => (
        <Img key={url} src={url} />
      ))}
    </div>
  );
}

function MiddleCols({ urls }: { urls: string[] }) {
  const heightOfCenter = 40; // percent
  const heightOfSides = (100 - heightOfCenter) / 2;

  // Split URLs into six parts
  const myParts = new Array(6).fill(null).map(() => []);

  for (let i = 0; i < urls.length; i++) {
    myParts[i % 6].push(urls[i]);
  }

  // This function contains some of the worst CSS I've ever written
  // Due to the fact that grid is not supported, nor most flexbox properties
  // So I had to do some awful scaling tricks to get the position right
  // I am sorry :(

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        padding: `0 ${padding}`,
        width: `${(3 * 100) / colCount}%`,
      }}
    >
      <div
        style={{
          height: `${heightOfSides}%`,
          display: "flex",
          fontSize: "10px",
        }}
      >
        {myParts.slice(0, 3).map((thisColUrls, colInd) => (
          <div
            key={colInd + Math.random()}
            style={{
              display: "flex",
              flexDirection: "column-reverse",
              justifyContent: "flex-end",
              width: "33.3333333%",
              padding:
                colInd === Math.floor(thisColUrls.length / 2)
                  ? `0 ${padding}`
                  : colInd === 0
                  ? `0 ${padding} 0 0`
                  : `0 0 0 ${padding}`,
              transform: "scale(1, -1)",
              marginTop: `${heightOfSides + 10}%`,
              transformOrigin: "top center",
            }}
          >
            {thisColUrls.map((url) => (
              <Img key={url} src={url} flipY />
            ))}
          </div>
        ))}
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          // backgroundColor: "red",
          // opacity: 0.3,
          width: "100%",
          height: `${heightOfCenter}%`,
        }}
      >
        {paiLogo}
      </div>
      <div
        style={{
          height: `${heightOfSides}%`,
          display: "flex",
          fontSize: "10px",
        }}
      >
        {myParts.slice(3, 6).map((thisColUrls, colInd) => (
          <div
            key={colInd + Math.random()}
            style={{
              display: "flex",
              flexDirection: "column-reverse",
              justifyContent: "flex-end",
              width: "33.3333333%",
              padding:
                colInd === Math.floor(thisColUrls.length / 2)
                  ? `0 ${padding}`
                  : colInd === 0
                  ? `0 ${padding} 0 0`
                  : `0 0 0 ${padding}`,
            }}
          >
            {thisColUrls.map((url) => (
              <Img key={url} src={url} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

function Img({ src, flipY }: { src: string; flipY?: boolean }) {
  return (
    <div
      style={{
        position: "relative",
        display: "flex",
        overflow: "visible",
      }}
    >
      <img
        src={src}
        style={{
          width: "100%",
          margin: `${padding} 0`,
          borderRadius: "5px",
          ...(flipY ? { transform: "scale(1, -1)" } : {}),
        }}
      />
    </div>
  );
}

